set name = #{name,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, age
from user_info
where id = #{id,jdbcType=VARCHAR}

